definition.script:
node ("linux") {
   // Mark the code checkout 'stage'....
   stage 'Checkout'

   // Get some code from a GitHub repository
   checkout([$class: 'GitSCM', branches: [[name: '*/master']], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/jenkinsci/git-plugin'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'CloneOption', depth: 0, noTags: false, reference: '/var/lib/git/mwaite/jenkins/git-plugin.git', shallow: false, timeout: 1], [$class: 'CleanBeforeCheckout'], [$class: 'PruneStaleBranch'], [$class: 'AuthorInChangelog']], gitTool: 'Default', submoduleCfg: [], userRemoteConfigs: [[url: 'git://github.com/jenkinsci/git-plugin.git']]])

   stage 'Test'
   // Verify objects directory is small
   sh "if [ `du -s .git/objects/ | awk '{print \$1}'` -lt 1000 ]; then echo objects less than 1000, test passed; ls -altr .git/objects/*; else echo objects too large; ls -altr .git/objects/; exit 1; fi"
}
